mapper.select:
SELECT p.PRODUCT_ID, p.PRODUCT_NAME, p.PRODUCT_PRICE, group_concat(img.PRODUCT_IMAGE_FILE_NAME ORDER BY img.PRODUCT_IMAGE_FILE_NAME) product_image_file_name
FROM product p INNER JOIN product_image img
ON p.product_id = img.product_id
WHERE img.PRODUCT_IMAGE_FROUTE = 'list'

	AND p.BLOOMING_SEASON = #{blooming_season}


	AND p.PET_FRIENDLY = #{pet_friendly}


	AND p.EASY_CARE = #{easy_care}


	AND p.BLOOMING_TIME = #{blooming_time}


	AND p.PRODUCT_LIGHT = #{product_light}

GROUP BY p.PRODUCT_ID

SELECT count(PRODUCT_ID) prod_cate_quan
FROM product


		WHERE BLOOMING_SEASON = #{blooming_season}
		GROUP BY BLOOMING_SEASON


		WHERE PET_FRIENDLY = #{pet_friendly}
		GROUP BY PET_FRIENDLY


		WHERE EASY_CARE = #{easy_care}
		GROUP BY EASY_CARE


		WHERE BLOOMING_TIME = #{blooming_time}
		GROUP BY BLOOMING_TIME


		GROUP BY PRODUCT_STATE
		HAVING PRODUCT_STATE = 'Y'

SELECT p.PRODUCT_ID, p.PRODUCT_NAME, p.PRODUCT_PRICE, group_concat(img.PRODUCT_IMAGE_FILE_NAME) product_image_file_name FROM product p INNER JOIN product_image img ON p.product_id = img.product_id
LEFT JOIN (SELECT ot.PRODUCT_ID, sum(ot.ORDER_PRODUCT_PRICE) sumO FROM order_table ot GROUP BY ot.PRODUCT_ID ORDER BY sumO DESC) oHigh ON p.PRODUCT_ID = oHigh.product_id
LEFT JOIN (SELECT r.PRODUCT_ID, count(r.PRODUCT_ID) cntR FROM reviews r WHERE r.REVIEWS_ID LIKE 'R%' GROUP BY r.PRODUCT_ID ORDER BY sumR DESC) rMany ON p.PRODUCT_ID = rMany.product_id
LEFT JOIN (SELECT l.PRODUCT_ID, count(l.PRODUCT_ID) cntL FROM love l GROUP BY l.PRODUCT_ID ORDER BY cntL DESC) lHigh ON p.PRODUCT_ID = lHigh.product_id
WHERE img.PRODUCT_IMAGE_froute = 'list'
AND p.EASY_CARE = #{easy_care}
AND p.PET_FRIENDLY = #{pet_friendly}
AND p.PRODUCT_LIGHT = #{product_light}
GROUP BY p.PRODUCT_ID
ORDER BY oHigh.sumO DESC ORDER BY rMany.cntR DESC ORDER BY lHigh.cntL DESC ORDER BY p.PRODUCT_PRICE DESC ORDER BY p.PRODUCT_PRICE ASC ORDER BY p.PRODUCT_ID ASC

SELECT PRODUCT_ID
FROM love
WHERE MEMBER_ID = #{member_id}
AND PRODUCT_ID = #{product_id}

mapper.delete:
DELETE FROM love
WHERE MEMBER_ID = #{member_id}
AND PRODUCT_ID = #{product_id}
