mapper.select:
 	SELECT IF(COUNT(*) > 0, 1, 0) as review_permission
		FROM order_table 
	WHERE member_id = #{member_id} 
	  AND product_id = #{product_id}

SELECT * FROM ( SELECT @row_number:=@row_number+1 AS no, b.* FROM ( SELECT * FROM reviews
reviews_title LIKE CONCAT('%', #{title}, '%')
) b, (SELECT @row_number:=0) as r ) ranked WHERE no BETWEEN ${ revi_start } AND ${ revi_end }
