mapper.update:
UPDATE shopping_cart
SET SHOPPING_CART_PRODUCT_QUANTITY = #{shopping_cart_product_quantity}
WHERE SHOPPING_CART_ID = #{shopping_cart_id}

mapper.select:
SELECT selected
FROM shopping_cart
WHERE SHOPPING_CART_ID = #{shopping_cart_id}

mapper.update:
UPDATE shopping_cart
SET selected = 1
WHERE shopping_cart_id = #{shopping_cart_id}

UPDATE shopping_cart
SET selected = 0
WHERE shopping_cart_id = #{shopping_cart_id}
