mapper.insert:

INSERT INTO MEMBER
(
	 MEMBER_EMAIL
	, MEMBER_PASSWORD
	, MEMBER_NAME
	, MEMBER_REGISTER_DATE
) 
VALUES
(
	 #{member_email}
	, #{member_password}
	, #{member_name}
	, NOW()
)

mapper.select:
select *
from member
where member_email = #{member_email}
and member_password = #{member_password}
